  1→"use client";
  2→
  3→import { useState } from "react";
  4→import { cn } from "@/lib/utils";
  5→import { ToolUsagePill } from "./tool-badge";
  6→import type { Message } from "@/types/checkpoint";
  7→
  8→interface TranscriptViewerProps {
  9→  messages: Message[];
 10→  agentName?: string;
 11→  agentPercent?: number;
 12→  className?: string;
 13→}
 14→
 15→interface SessionStats {
 16→  prompt: string;
 17→  steps: number;
 18→  toolCount: number;
 19→  toolNames: string[];
 20→  totalTokens: number;
 21→  duration: string;
 22→  startTime: string;
 23→}
 24→
 25→const COLLAPSE_THRESHOLD = 500;
 26→
 27→function extractToolNames(content: string): string[] {
 28→  const matches = content.match(/\[Tool: (.+?)\]/g);
 29→  if (!matches) return [];
 30→  return matches.map((m) => m.replace("[Tool: ", "").replace("]", ""));
 31→}
 32→
 33→function stripToolPatterns(content: string): string {
 34→  return content
 35→    .replace(/\[Tool: .+?\]/g, "")
 36→    .replace(/\[Tool Result: [\s\S]*?\]/g, "")
 37→    .replace(/\n{3,}/g, "\n\n")
 38→    .trim();
 39→}
 40→
 41→function formatDuration(ms: number): string {
 42→  const seconds = Math.floor(ms / 1000);
 43→  if (seconds < 60) return `${seconds}s`;
 44→  const minutes = Math.floor(seconds / 60);
 45→  const remainingSeconds = seconds % 60;
 46→  if (minutes < 60) return `${minutes}m ${remainingSeconds}s`;
 47→  const hours = Math.floor(minutes / 60);
 48→  const remainingMinutes = minutes % 60;
 49→  return `${hours}h ${remainingMinutes}m`;
 50→}
 51→
 52→function computeSessionStats(messages: Message[]): SessionStats {
 53→  const humanMessages = messages.filter(
 54→    (m) => m.role === "human" || m.role === "user"
 55→  );
 56→  const prompt =
 57→    humanMessages.length > 0
 58→      ? humanMessages[0].content.slice(0, 80) +
 59→        (humanMessages[0].content.length > 80 ? "..." : "")
 60→      : "Session";
 61→
 62→  const allToolNames: string[] = [];
 63→  for (const msg of messages) {
 64→    allToolNames.push(...extractToolNames(msg.content));
 65→  }
 66→  const uniqueTools = [...new Set(allToolNames)];
 67→
 68→  const totalTokens = messages.reduce((sum, m) => sum + (m.tokens || 0), 0);
 69→
 70→  let duration = "—";
 71→  if (messages.length >= 2) {
 72→    const first = new Date(messages[0].timestamp).getTime();
 73→    const last = new Date(messages[messages.length - 1].timestamp).getTime();
 74→    if (!isNaN(first) && !isNaN(last) && last > first) {
 75→      duration = formatDuration(last - first);
 76→    }
 77→  }
 78→
 79→  let startTime = "";
 80→  if (messages.length > 0) {
 81→    const d = new Date(messages[0].timestamp);
 82→    if (!isNaN(d.getTime())) {
 83→      startTime = d.toLocaleTimeString([], {
 84→        hour: "2-digit",
 85→        minute: "2-digit",
 86→      });
 87→    }
 88→  }
 89→
 90→  return {
 91→    prompt,
 92→    steps: humanMessages.length,
 93→    toolCount: uniqueTools.length,
 94→    toolNames: allToolNames,
 95→    totalTokens,
 96→    duration,
 97→    startTime,
 98→  };
 99→}
100→
101→function AttributionBar({
102→  percent,
103→  className,
104→}: {
105→  percent: number;
106→  className?: string;
107→}) {
108→  return (
109→    <div className={cn("flex items-center gap-1.5", className)}>
110→      <div className="h-1.5 w-14 overflow-hidden rounded-full bg-border">
111→        <div
112→          className="h-full rounded-full bg-accent-orange"
113→          style={{ width: `${Math.min(100, Math.max(0, percent))}%` }}
114→        />
115→      </div>
116→      <span className="text-xs text-muted">{percent}% AI</span>
117→    </div>
118→  );
119→}
120→
121→function HumanAvatar() {
122→  return (
123→    <div className="flex h-8 w-8 shrink-0 items-center justify-center rounded-full bg-surface-light border border-border">
124→      <svg
125→        width="16"
126→        height="16"
127→        viewBox="0 0 24 24"
128→        fill="none"
129→        stroke="currentColor"
130→        strokeWidth="2"
131→        strokeLinecap="round"
132→        strokeLinejoin="round"
133→        className="text-muted"
134→      >
135→        <path d="M19 21v-2a4 4 0 0 0-4-4H9a4 4 0 0 0-4 4v2" />
136→        <circle cx="12" cy="7" r="4" />
137→      </svg>
138→    </div>
139→  );
140→}
141→
142→function AssistantAvatar() {
143→  return (
144→    <div className="flex h-8 w-8 shrink-0 items-center justify-center rounded-full bg-accent-orange/15 border border-accent-orange/30">
145→      <span className="text-xs font-bold text-accent-orange">A\</span>
146→    </div>
147→  );
148→}
149→
150→function MessageRow({ message }: { message: Message }) {
151→  const [expanded, setExpanded] = useState(false);
152→  const isHuman = message.role === "human" || message.role === "user";
153→  const toolNames = extractToolNames(message.content);
154→  const cleanContent = isHuman
155→    ? message.content
156→    : stripToolPatterns(message.content);
157→  const isLong = cleanContent.length > COLLAPSE_THRESHOLD;
158→  const displayContent =
159→    !expanded && isLong
160→      ? cleanContent.slice(0, COLLAPSE_THRESHOLD) + "..."
161→      : cleanContent;
162→
163→  return (
164→    <div className="flex gap-3 py-3">
165→      {isHuman ? <HumanAvatar /> : <AssistantAvatar />}
166→      <div className={cn("flex-1 min-w-0", !isHuman && "border-l-2 border-accent-orange/60 pl-3")}>
167→        <div className="mb-1 flex items-center gap-2">
168→          <span className="text-xs font-medium text-foreground">
169→            {isHuman ? "You" : "Assistant"}
170→          </span>
171→          {message.tokens != null && message.tokens > 0 && (
172→            <span className="text-xs text-muted">
173→              {message.tokens.toLocaleString()} tokens
174→            </span>
175→          )}
176→        </div>
177→        {displayContent && (
178→          <div className="whitespace-pre-wrap text-sm text-foreground/90 leading-relaxed">
179→            {displayContent}
180→          </div>
181→        )}
182→        {!isHuman && toolNames.length > 0 && (
183→          <div className="mt-2">
184→            <ToolUsagePill toolNames={toolNames} />
185→          </div>
186→        )}
187→        {isLong && (
188→          <button
