  1→import { Octokit } from "@octokit/rest";
  2→import type { CheckpointMetadata } from "@/types/checkpoint";
  3→
  4→const CHECKPOINT_BRANCH = "partio/checkpoints/v1";
  5→
  6→export async function listCheckpoints(
  7→  octokit: Octokit,
  8→  owner: string,
  9→  repo: string
 10→): Promise<CheckpointMetadata[]> {
 11→  const { data: ref } = await octokit.git.getRef({
 12→    owner,
 13→    repo,
 14→    ref: `heads/${CHECKPOINT_BRANCH}`,
 15→  });
 16→
 17→  const { data: commit } = await octokit.git.getCommit({
 18→    owner,
 19→    repo,
 20→    commit_sha: ref.object.sha,
 21→  });
 22→
 23→  const { data: tree } = await octokit.git.getTree({
 24→    owner,
 25→    repo,
 26→    tree_sha: commit.tree.sha,
 27→    recursive: "1",
 28→  });
 29→
 30→  const metadataEntries = (tree.tree || []).filter(
 31→    (entry) =>
 32→      entry.path &&
 33→      /^[0-9a-f]{2}\/[0-9a-f]{10}\/metadata\.json$/.test(entry.path)
 34→  );
 35→
 36→  const checkpoints = await Promise.allSettled(
 37→    metadataEntries.map(async (entry) => {
 38→      const { data: blob } = await octokit.git.getBlob({
 39→        owner,
 40→        repo,
 41→        file_sha: entry.sha!,
 42→      });
 43→
 44→      const content = Buffer.from(blob.content, "base64").toString("utf-8");
 45→      return JSON.parse(content) as CheckpointMetadata;
 46→    })
 47→  );
 48→
 49→  return checkpoints
 50→    .filter(
 51→      (r): r is PromiseFulfilledResult<CheckpointMetadata> =>
 52→        r.status === "fulfilled"
 53→    )
 54→    .map((r) => r.value)
 55→    .sort(
 56→      (a, b) =>
 57→        new Date(b.created_at).getTime() - new Date(a.created_at).getTime()
 58→    );
 59→}
 60→
 61→export async function getCheckpoint(
 62→  octokit: Octokit,
 63→  owner: string,
 64→  repo: string,
 65→  checkpointId: string
 66→): Promise<CheckpointMetadata | null> {
 67→  const shard = checkpointId.slice(0, 2);
 68→  const rest = checkpointId.slice(2);
 69→
 70→  try {
 71→    const { data: ref } = await octokit.git.getRef({
 72→      owner,
 73→      repo,
 74→      ref: `heads/${CHECKPOINT_BRANCH}`,
 75→    });
 76→
 77→    const { data: commit } = await octokit.git.getCommit({
 78→      owner,
 79→      repo,
 80→      commit_sha: ref.object.sha,
 81→    });
 82→
 83→    const { data: tree } = await octokit.git.getTree({
 84→      owner,
 85→      repo,
 86→      tree_sha: commit.tree.sha,
 87→      recursive: "1",
 88→    });
 89→
 90→    const metadataPath = `${shard}/${rest}/metadata.json`;
 91→    const entry = (tree.tree || []).find((e) => e.path === metadataPath);
 92→
 93→    if (!entry?.sha) return null;
 94→
 95→    const { data: blob } = await octokit.git.getBlob({
 96→      owner,
 97→      repo,
 98→      file_sha: entry.sha,
 99→    });
100→
101→    const content = Buffer.from(blob.content, "base64").toString("utf-8");
102→    return JSON.parse(content) as CheckpointMetadata;
103→  } catch {
104→    return null;
105→  }
106→}
