  1→import { Octokit } from "@octokit/rest";
  2→import type { Message } from "@/types/checkpoint";
  3→
  4→const CHECKPOINT_BRANCH = "partio/checkpoints/v1";
  5→
  6→export async function getSessionData(
  7→  octokit: Octokit,
  8→  owner: string,
  9→  repo: string,
 10→  checkpointId: string
 11→): Promise<Message[]> {
 12→  const shard = checkpointId.slice(0, 2);
 13→  const rest = checkpointId.slice(2);
 14→
 15→  try {
 16→    const { data: ref } = await octokit.git.getRef({
 17→      owner,
 18→      repo,
 19→      ref: `heads/${CHECKPOINT_BRANCH}`,
 20→    });
 21→
 22→    const { data: commit } = await octokit.git.getCommit({
 23→      owner,
 24→      repo,
 25→      commit_sha: ref.object.sha,
 26→    });
 27→
 28→    const { data: tree } = await octokit.git.getTree({
 29→      owner,
 30→      repo,
 31→      tree_sha: commit.tree.sha,
 32→      recursive: "1",
 33→    });
 34→
 35→    const sessionPath = `${shard}/${rest}/0/full.jsonl`;
 36→    const entry = (tree.tree || []).find((e) => e.path === sessionPath);
 37→
 38→    if (!entry?.sha) return [];
 39→
 40→    const { data: blob } = await octokit.git.getBlob({
 41→      owner,
 42→      repo,
 43→      file_sha: entry.sha,
 44→    });
 45→
 46→    const content = Buffer.from(blob.content, "base64").toString("utf-8");
 47→    const lines = content.split("\n").filter((line) => line.trim());
 48→
 49→    const MESSAGE_TYPES = new Set(["user", "human", "assistant"]);
 50→
 51→    return lines
 52→      .map((line) => JSON.parse(line))
 53→      .filter((parsed: any) => MESSAGE_TYPES.has(parsed.type || parsed.role))
 54→      .map((parsed: any) => {
 55→        return {
 56→          role: parsed.type || parsed.role || "unknown",
 57→          content: stripSystemTags(extractText(parsed)),
 58→          timestamp: parsed.timestamp || "",
 59→          tokens: parsed.tokens,
 60→        } satisfies Message;
 61→      })
 62→      .filter((msg) => msg.content.trim() !== "");
 63→  } catch {
 64→    return [];
 65→  }
 66→}
 67→
 68→function extractContentBlocks(blocks: any[]): string {
 69→  return blocks
 70→    .map((block: any) => {
 71→      if (typeof block === "string") return block;
 72→      if (block?.type === "text") return block.text || "";
 73→      if (block?.type === "tool_use")
 74→        return `[Tool: ${block.name || "unknown"}]`;
 75→      if (block?.type === "tool_result") return "";
 76→      // Skip thinking, redacted_thinking, and unknown block types
 77→      return "";
 78→    })
 79→    .filter(Boolean)
 80→    .join("\n");
 81→}
 82→
 83→const SYSTEM_TAG_RE =
 84→  /<(local-command-caveat|command-name|command-message|command-args|local-command-stdout|system-reminder|user-prompt-submit-hook)>[\s\S]*?<\/\1>/g;
 85→
 86→function stripSystemTags(text: string): string {
 87→  return text.replace(SYSTEM_TAG_RE, "");
 88→}
 89→
 90→function extractText(entry: Record<string, unknown>): string {
 91→  // 1. Try contentBlocks (top-level array of {type, text})
 92→  if (Array.isArray(entry.contentBlocks)) {
 93→    return extractContentBlocks(entry.contentBlocks);
 94→  }
 95→
 96→  // 2. Try message field
 97→  if (entry.message != null) {
 98→    if (typeof entry.message === "string") return entry.message;
 99→    const msg = entry.message as any;
100→    if (typeof msg.content === "string") return msg.content;
101→    if (Array.isArray(msg.content)) {
102→      return extractContentBlocks(msg.content);
103→    }
104→  }
105→
106→  // 3. Try content field
107→  if (entry.content != null) {
108→    if (typeof entry.content === "string") return entry.content;
109→    if (Array.isArray(entry.content)) {
110→      return extractContentBlocks(entry.content as any[]);
111→    }
112→  }
113→
114→  return "";
115→}
