  8→// Write stores a checkpoint and its associated session data on the orphan branch.
  9→func (s *Store) Write(cp *Checkpoint, sessionData *SessionFiles) error {
 10→	shard := Shard(cp.ID)
 11→	rest := Rest(cp.ID)
 12→
 13→	// Hash all the blob objects
 14→	metaJSON, err := json.MarshalIndent(cp.ToMetadata(), "", "  ")
 15→	if err != nil {
 16→		return fmt.Errorf("marshaling metadata: %w", err)
 17→	}
 18→
 19→	metaHash, err := s.hashObject(string(metaJSON))
 20→	if err != nil {
 21→		return fmt.Errorf("hashing metadata: %w", err)
 22→	}
 23→
 24→	contentHashHash, err := s.hashObject(sessionData.ContentHash)
 25→	if err != nil {
 26→		return fmt.Errorf("hashing content_hash: %w", err)
 27→	}
 28→
 29→	contextHash, err := s.hashObject(sessionData.Context)
 30→	if err != nil {
 31→		return fmt.Errorf("hashing context: %w", err)
 32→	}
 33→
 34→	fullHash, err := s.hashObject(sessionData.FullJSONL)
 35→	if err != nil {
 36→		return fmt.Errorf("hashing full.jsonl: %w", err)
 37→	}
 38→
 39→	sessionMetaJSON, err := json.MarshalIndent(sessionData.Metadata, "", "  ")
 40→	if err != nil {
 41→		return fmt.Errorf("marshaling session metadata: %w", err)
 42→	}
 43→	sessionMetaHash, err := s.hashObject(string(sessionMetaJSON))
 44→	if err != nil {
 45→		return fmt.Errorf("hashing session metadata: %w", err)
 46→	}
 47→
 48→	promptHash, err := s.hashObject(sessionData.Prompt)
 49→	if err != nil {
 50→		return fmt.Errorf("hashing prompt: %w", err)
 51→	}
 52→
 53→	diffHash, err := s.hashObject(sessionData.Diff)
 54→	if err != nil {
 55→		return fmt.Errorf("hashing diff: %w", err)
 56→	}
 57→
 58→	planHash, err := s.hashObject(sessionData.Plan)
 59→	if err != nil {
 60→		return fmt.Errorf("hashing plan: %w", err)
 61→	}
 62→
 63→	// Build session subtree (0/)
 64→	sessionTree, err := s.mktree([]treeEntry{
 65→		{mode: "100644", typ: "blob", hash: contentHashHash, name: "content_hash.txt"},
 66→		{mode: "100644", typ: "blob", hash: contextHash, name: "context.md"},
 67→		{mode: "100644", typ: "blob", hash: diffHash, name: "diff.patch"},
 68→		{mode: "100644", typ: "blob", hash: fullHash, name: "full.jsonl"},
 69→		{mode: "100644", typ: "blob", hash: sessionMetaHash, name: "metadata.json"},
 70→		{mode: "100644", typ: "blob", hash: planHash, name: "plan.md"},
 71→		{mode: "100644", typ: "blob", hash: promptHash, name: "prompt.txt"},
 72→	})
 73→	if err != nil {
 74→		return fmt.Errorf("creating session tree: %w", err)
 75→	}
 76→
 77→	// Build checkpoint subtree (<rest>/)
 78→	cpTree, err := s.mktree([]treeEntry{
 79→		{mode: "100644", typ: "blob", hash: metaHash, name: "metadata.json"},
 80→		{mode: "040000", typ: "tree", hash: sessionTree, name: "0"},
 81→	})
 82→	if err != nil {
 83→		return fmt.Errorf("creating checkpoint tree: %w", err)
 84→	}
 85→
 86→	// Get current tree of the branch
 87→	currentTree, err := s.getCurrentTree()
 88→	if err != nil {
 89→		return fmt.Errorf("getting current tree: %w", err)
 90→	}
 91→
 92→	// Build new root tree incorporating the existing tree + new shard entry
 93→	newRoot, err := s.addToTree(currentTree, shard, rest, cpTree)
 94→	if err != nil {
 95→		return fmt.Errorf("building root tree: %w", err)
 96→	}
 97→
 98→	// Create commit
 99→	parentCommit, err := s.git("rev-parse", checkpointBranch)
100→	if err != nil {
101→		return fmt.Errorf("getting parent commit: %w", err)
102→	}
103→
104→	commitMsg := fmt.Sprintf("checkpoint: %s", cp.ID)
105→	commitHash, err := s.git("commit-tree", newRoot, "-p", parentCommit, "-m", commitMsg)
106→	if err != nil {
107→		return fmt.Errorf("creating commit: %w", err)
108→	}
109→
110→	// Update ref
111→	_, err = s.git("update-ref", "refs/heads/"+checkpointBranch, commitHash)
112→	if err != nil {
113→		return fmt.Errorf("updating ref: %w", err)
114→	}
115→
116→	return nil
117→}
