            </svg>
            <span className="text-foreground">{checkpoint.branch}</span>
          </span>

          {totalTokens > 0 && (
            <>
              <span className="text-muted/60">·</span>
              <span>{totalTokens.toLocaleString()} tokens</span>
            </>
          )}

          {checkpoint.agent && (
