+                {children}
+              </td>
+            ),
+            hr: () => <hr className="border-border my-4" />,
+            strong: ({ children }) => (
+              <strong className="font-semibold text-foreground">{children}</strong>
+            ),
+          }}
+        >
+          {plan}
+        </ReactMarkdown>
+      </div>
+    </div>
+  );
+}
